import React from "react";

const Posts = ({ posts, loading, pageNumber }) => {
  if (loading) {
    return <h2>Loading...</h2>;
  }

  let counter = pageNumber;
  return (
    <ul className="list-group mb-4">
      {posts.map(post => (
        <li key={post.id} className="list-group-item text-left">
          {pageNumber + counter} . {post.question} {counter++}
          <p>{post.answer}</p>
        </li>
      ))}
    </ul>
  );
};

export default Posts;
